import { Pipe, PipeTransform } from '@angular/core';
import { LanguageType } from '../services/lang.service';
import { ItemsService } from '../services/items.service';
import { PropertiesTranslationPipe } from './properties-translation.pipe';

@Pipe({
  name: 'addOnUuidsToAddOns',
})
export class AddOnUuidsToAddOnsPipe implements PipeTransform {
  constructor(
    private itemsService: ItemsService,
    private propertiesTranslationPipe: PropertiesTranslationPipe
  ) {}
  transform(values: string[], lang: LanguageType) {
    return values
      .map((addOnUuid) => this.itemsService.getAddOnByUuid(addOnUuid))
      .map((addOn) =>
        this.propertiesTranslationPipe.transform(addOn, lang, 'Name')
      )
      .join(' + ');
  }
}

import { Pipe, PipeTransform } from '@angular/core';
import { CartItem, CartService } from '../services/cart.service';

@Pipe({
  name: 'previewItemCost',
  pure: false,
})
export class PreviewItemCostPipe implements PipeTransform {
  constructor(private cartService: CartService) {}
  transform(value?: CartItem, permutationUuids?: string[]): string {
    return value
      ? this.cartService.calcCartItemCost(value, permutationUuids)
      : '0.0 GTQ';
  }
}

import { Injectable, Pipe, PipeTransform } from '@angular/core';
import { LanguageType } from '../services/lang.service';

@Pipe({
  name: 'propertiesTranslation',
})
@Injectable({
  providedIn: 'root',
})
export class PropertiesTranslationPipe implements PipeTransform {
  transform(value: any, lang: LanguageType, keySuffix: 'Details' | 'Name') {
    return value[`${lang}${keySuffix}`];
  }
}

