import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { BehaviorSubject, Observable, catchError, map, of, tap } from 'rxjs';
import { NotificationsService } from './notifications.service';
import { TranslateService } from '@ngx-translate/core';

interface AuthResponse {
  auth: boolean;
  token: string;
}

export interface GuestDetails {
  confirmation: boolean;
  email?: string;
  hebrewname: string;
  id: string;
  participants: string;
  phone: string;
  transport: boolean;
  username?: string;
}

@Injectable({
  providedIn: 'root',
})
export class AuthService {
  private _guestDetails$ = new BehaviorSubject<GuestDetails | null>(null);

  get guestDetails$(): Observable<GuestDetails | null> {
    return this._guestDetails$.asObservable();
  }

  get guestDetails(): GuestDetails | null {
    return this._guestDetails$.value;
  }

  constructor(
    private http: HttpClient,
    private notificationsService: NotificationsService,
    private translate: TranslateService
  ) {}

  login(username: string, password: string): Observable<boolean> {
    return this.http
      .post<AuthResponse>('/api/login', { username, password })
      .pipe(
        tap((authResponse: AuthResponse) => {
          window.localStorage.removeItem('auth-user');
          window.localStorage.setItem(
            'auth-user',
            JSON.stringify(authResponse.token)
          );
        }),
        map((authResponse: AuthResponse) => {
          return authResponse.auth;
        }),
        catchError((error) => {
          if (error.status === 404) {
            this.notificationsService.setNotification({
              type: 'ERROR',
              message: this.translate.instant(
                `notifications.errors.${error.error.message}`,
                { user: error.error.params }
              ),
            });
          } else if (error.status === 401) {
            this.notificationsService.setNotification({
              type: 'ERROR',
              message: this.translate.instant(
                `notifications.errors.${error.error.message}`,
                { user: error.error.params }
              ),
            });
          } else {
            this.notificationsService.setNotification({
              type: 'ERROR',
              message: this.translate.instant('notifications.errors.general'),
            });
          }

          console.error(error);
          return of(false);
        })
      );
  }
}

import { Injectable } from '@angular/core';
import { BehaviorSubject, Observable, map, of } from 'rxjs';
import { Item } from '../travler/travler.models';

export interface CartItem {
  itemUuid: string;
  item: Item;
  items: Map<string, string[]>;
}

interface SerializableCartItem {
  itemUuid: string;
  item: Item;
  items: Array<[string, string[]]>;
}

interface CartState {
  items: SerializableCartItem[];
  timestamp: number;
}

@Injectable({
  providedIn: 'root',
})
export class CartService {
  private readonly CART_KEY = 'shopping_cart';
  private readonly EXPIRY_TIME = 15 * 60 * 1000;

  private cartItems = new BehaviorSubject<CartItem[]>([]);
  public cartItems$ = this.cartItems.asObservable();

  constructor() {
    this.initializeCart();
    this.setupExpiryCheck();
  }

  private serializeCartItems(items: CartItem[]): SerializableCartItem[] {
    return items.map((item) => ({
      itemUuid: item.itemUuid,
      item: item.item,
      items: Array.from(item.items.entries()),
    }));
  }

  private deserializeCartItems(items: SerializableCartItem[]): CartItem[] {
    return items.map((item) => ({
      itemUuid: item.itemUuid,
      item: item.item,
      items: new Map(item.items),
    }));
  }

  private initializeCart(): void {
    const savedCart = localStorage.getItem(this.CART_KEY);
    if (savedCart) {
      try {
        const cartState: CartState = JSON.parse(savedCart);
        const now = Date.now();

        if (now - cartState.timestamp <= this.EXPIRY_TIME) {
          const items = this.deserializeCartItems(cartState.items);
          this.cartItems.next(items);
        } else {
          this.clearCart();
        }
      } catch (error) {
        console.error('Error parsing cart from localStorage:', error);
        this.clearCart();
      }
    }
  }

  private setupExpiryCheck(): void {
    setInterval(() => {
      const savedCart = localStorage.getItem(this.CART_KEY);
      if (savedCart) {
        const cartState: CartState = JSON.parse(savedCart);
        const now = Date.now();

        if (now - cartState.timestamp > this.EXPIRY_TIME) {
          this.clearCart();
        }
      }
    }, 60000);
  }

  private saveToLocalStorage(items: CartItem[]): void {
    const cartState: CartState = {
      items: this.serializeCartItems(items),
      timestamp: Date.now(),
    };
    localStorage.setItem(this.CART_KEY, JSON.stringify(cartState));
  }

  private generateCartItemId(): string {
    return `${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }

  addItem(item: Item, selectedAddOns: string[] = []): void {
    const currentItems = this.cartItems.value;
    const cartItemId = this.generateCartItemId();

    const existingItemIndex = currentItems.findIndex(
      (i) => i.itemUuid === item.uuid
    );

    if (existingItemIndex > -1) {
      const updatedItems = [...currentItems];
      updatedItems[existingItemIndex].items.set(cartItemId, selectedAddOns);
      this.cartItems.next(updatedItems);
      this.saveToLocalStorage(updatedItems);
    } else {
      const newCartItem: CartItem = {
        itemUuid: item.uuid,
        item: item,
        items: new Map([[cartItemId, selectedAddOns]]),
      };
      this.cartItems.next([...currentItems, newCartItem]);
      this.saveToLocalStorage([...currentItems, newCartItem]);
    }
  }

  removeVariant(itemUuid: string, cartItemId: string): void {
    const currentItems = this.cartItems.value;
    const itemIndex = currentItems.findIndex((i) => i.itemUuid === itemUuid);

    if (itemIndex > -1) {
      const updatedItems = [...currentItems];
      updatedItems[itemIndex].items.delete(cartItemId);

      if (updatedItems[itemIndex].items.size === 0) {
        updatedItems.splice(itemIndex, 1);
      }

      this.cartItems.next(updatedItems);
      this.saveToLocalStorage(updatedItems);
    }
  }

  updateVariantAddOns(
    itemUuid: string,
    cartItemId: string,
    newAddOns: string[]
  ): void {
    const currentItems = this.cartItems.value;
    const itemIndex = currentItems.findIndex((i) => i.itemUuid === itemUuid);

    if (itemIndex > -1 && currentItems[itemIndex].items.has(cartItemId)) {
      const updatedItems = [...currentItems];
      updatedItems[itemIndex].items.set(cartItemId, newAddOns);
      this.cartItems.next(updatedItems);
      this.saveToLocalStorage(updatedItems);
    }
  }

  clearCart(): void {
    this.cartItems.next([]);
    localStorage.removeItem(this.CART_KEY);
  }

  getCartItem(itemUuid: string): CartItem | undefined {
    return this.cartItems.value.find(
      (cartItem) => cartItem.itemUuid === itemUuid
    );
  }

  getItemCount(itemUuid: string): Observable<number> {
    if (!itemUuid) {
      return of(0);
    }

    return this.cartItems$.pipe(
      map((items) => {
        const item = items.find((i) => i.itemUuid === itemUuid);
        return item ? item.items.size : 0;
      })
    );
  }

  getTotalItemsCount(): Observable<number> {
    return this.cartItems$.pipe(
      map((items) => items.reduce((total, item) => total + item.items.size, 0))
    );
  }

  getVariantsForItem(
    itemUuid: string
  ): Observable<Map<string, string[]> | undefined> {
    return this.cartItems$.pipe(
      map((items) => items.find((i) => i.itemUuid === itemUuid)?.items)
    );
  }

  calcCartItemCost(cartItem: CartItem, permutationUuids?: string[]): string {
    const basePrice = cartItem.item.price;
    const freeAddons = cartItem.item.freeAvailableAddOns || 0;
    const pricePerAddOn = cartItem.item.addOnPrice || 0;
    let totalPrice = 0;
    cartItem.items.forEach((value, permutationUuid) => {
      if (permutationUuids) {
        if (permutationUuids.includes(permutationUuid)) {
          totalPrice += basePrice;
          if (value.length) {
            const addOnsNeedsPay = value.length - freeAddons;
            totalPrice += addOnsNeedsPay * pricePerAddOn;
          }
        }
      } else {
        totalPrice += basePrice;
        if (value.length) {
          const addOnsNeedsPay = value.length - freeAddons;
          totalPrice += addOnsNeedsPay * pricePerAddOn;
        }
      }
    });

    return `${totalPrice.toFixed(2)} GTQ`;
  }
}

import { Component, EventEmitter, Input, Output } from '@angular/core';
import { CartItem, CartService } from '../../services/cart.service';
import { CommonModule } from '@angular/common';
import { MaterialModule } from '../../material.module';
import { LanguageDirectionDirective } from '../../directives/language-direction.directive';
import { PropertiesTranslationPipe } from '../../pipes/properties-translation.pipe';
import { Observable } from 'rxjs';
import { LanguageType, LanguageService } from '../../services/lang.service';
import { TranslateModule } from '@ngx-translate/core';
import { AddOnUuidsToAddOnsPipe } from '../../pipes/add-on-uuids-to-add-ons.pipe';
import { PreviewItemCostPipe } from '../../pipes/preview-item-cost.pipe';

interface GroupedCartItem {
  addOns: string[];
  permutationUuids: string[];
  items: CartItemPermutation[];
  count: number;
}

interface CartItemPermutation {
  id: string;
  addOns: string[];
}

@Component({
  selector: 'app-item-preview',
  imports: [
    CommonModule,
    MaterialModule,
    LanguageDirectionDirective,
    PropertiesTranslationPipe,
    AddOnUuidsToAddOnsPipe,
    PreviewItemCostPipe,
    TranslateModule,
  ],
  templateUrl: './item-preview.component.html',
  styleUrl: './item-preview.component.scss',
})
export class ItemPreviewComponent {
  // TODO: change to group by add-ons uuids
  private _cartItem: CartItem | undefined;
  @Input() set cartItem(value: CartItem) {
    this._cartItem = value;
    this.groupedCartItem = this.groupSimilarItems();
  }
  get cartItem(): CartItem | undefined {
    return this._cartItem;
  }
  @Input() expanded = false;

  @Output() opened = new EventEmitter<number>();

  public lang$: Observable<LanguageType> = new Observable<LanguageType>();
  public groupedCartItem: GroupedCartItem[] = [];

  constructor(
    private languageService: LanguageService,
    private cartService: CartService
  ) {}

  ngOnInit(): void {
    this.lang$ = this.languageService.currentLanguage$;
  }

  groupSimilarItems(): GroupedCartItem[] {
    if (!this.cartItem?.items) return [];

    const groupMap = new Map<string, GroupedCartItem>();

    this.cartItem.items.forEach((addOns: string[], id: string) => {
      console.log(id, addOns);

      const sortedKey = [...addOns].sort().join(',');
      console.log(sortedKey);

      if (!groupMap.has(sortedKey)) {
        groupMap.set(sortedKey, {
          addOns: [...addOns].sort(),
          permutationUuids: [id],
          items: [],
          count: 0,
        });
      }

      const group = groupMap.get(sortedKey)!;
      group.items.push({ id, addOns });
      group.count++;
      group.permutationUuids.push(id);
    });

    return Array.from(groupMap.values());
  }

  removeItemGroup(group: GroupedCartItem): void {
    if (!this.cartItem?.itemUuid) return;

    group.items.forEach((item) => {
      this.cartService.removeVariant(this.cartItem!.itemUuid, item.id);
    });
    this.groupedCartItem = this.groupSimilarItems();
  }
}

