import { CommonModule } from '@angular/common';
import { ChangeDetectionStrategy, Component, Inject } from '@angular/core';
import { MaterialModule } from '../../material.module';
import { AddOn, Item } from '../travler.models';
import { MAT_DIALOG_DATA, MatDialogRef } from '@angular/material/dialog';
import { ItemsService } from '../../services/items.service';
import { FormsModule } from '@angular/forms';
import { LanguageService, LanguageType } from '../../services/lang.service';
import { Observable } from 'rxjs';
import { LanguageDirectionDirective } from '../../directives/language-direction.directive';
import { PropertiesTranslationPipe } from '../../pipes/properties-translation.pipe';
import { TranslateModule } from '@ngx-translate/core';

interface CheckboxState {
  selected: boolean;
}
type ModalAddOn = CheckboxState & AddOn;

@Component({
  selector: 'app-item-add-ons-selections',
  standalone: true,
  imports: [
    CommonModule,
    FormsModule,
    MaterialModule,
    LanguageDirectionDirective,
    PropertiesTranslationPipe,
    TranslateModule,
  ],
  templateUrl: './item-add-ons-selections.component.html',
  styleUrls: ['./item-add-ons-selections.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class ItemAddOnsSelectionsComponent {
  public lang$: Observable<LanguageType> | undefined;
  public availableAddOns: ModalAddOn[] = [];
  public selectedAddOnUuids: string[] = [];
  public pricePerAddOn: number | undefined;
  public freeAvailableAddOns: number | undefined;
  public selectedFreeAddOnUuids: string[] = [];

  constructor(
    @Inject(MAT_DIALOG_DATA) public data: Partial<Item>,
    private languageService: LanguageService,
    private itemsService: ItemsService,
    private dialogRef: MatDialogRef<ItemAddOnsSelectionsComponent>
  ) {
    this.lang$ = this.languageService.currentLanguage$;

    this.pricePerAddOn = data?.addOnPrice;
    this.freeAvailableAddOns = data?.freeAvailableAddOns;
    this.availableAddOns = (data.availableAddOnUuids as string[])
      .map((addOnUuid) => this.itemsService.getAddOnByUuid(addOnUuid))
      .filter((addOn) => !!addOn)
      .map((addOn) => ({
        selected: false,
        ...addOn,
      }));
  }

  onCheckboxChange(addOn: ModalAddOn) {
    if (addOn.selected) {
      this.selectedAddOnUuids.push(addOn.uuid);
      // TODO: display current cost
      // if (
      //   this.selectedFreeAddOnUuids.length !== this.freeAvailableAddOns &&
      //   !this.selectedFreeAddOnUuids.includes(addOn.uuid)
      // ) {
      //   this.selectedFreeAddOnUuids.push(addOn.uuid);
      // }
    } else {
      const index = this.selectedAddOnUuids.indexOf(addOn.uuid);
      if (index >= 0) {
        this.selectedAddOnUuids.splice(index, 1);
      }
    }
  }

  onSave() {
    this.dialogRef.close(this.selectedAddOnUuids);
  }
}

import { Component } from '@angular/core';
import { MaterialModule } from '../../material.module';
import { MenuIconComponent } from '../../components/menu-icon/menu-icon.component';
import { LanguagePickerComponent } from '../../components/language-picker/language-picker.component';
import { NavigationEnd, Router, RouterOutlet } from '@angular/router';
import { CommonModule } from '@angular/common';
import { TranslateModule } from '@ngx-translate/core';
import { filter, map, Observable, startWith } from 'rxjs';
import {
  LanguageDirection,
  LanguageService,
  LanguageType,
} from '../../services/lang.service';
import { LanguageDirectionDirective } from '../../directives/language-direction.directive';
import { PropertiesTranslationPipe } from '../../pipes/properties-translation.pipe';

interface DashboardMenuButton {
  titleKey: string;
  icon: string;
  route: string;
}

const TRANSLATE_KEY_PREFIX = 'dashboard.menu.buttons.titles';
@Component({
  selector: 'app-dashboard',
  imports: [
    CommonModule,
    MaterialModule,
    MenuIconComponent,
    LanguagePickerComponent,
    RouterOutlet,
    TranslateModule,
    LanguageDirectionDirective,
  ],
  templateUrl: './dashboard.component.html',
  styleUrl: './dashboard.component.scss',
})
export class DashboardComponent {
  public lang$: Observable<LanguageType> = new Observable<LanguageType>();
  public selectedMenuButton: DashboardMenuButton | undefined;
  public stickyLeft = false;

  constructor(
    private router: Router,
    private languageService: LanguageService
  ) {
    this.lang$ = this.languageService.currentLanguage$;
    this.router.events
      .pipe(
        filter(
          (event): event is NavigationEnd => event instanceof NavigationEnd
        ),
        map((event: NavigationEnd) => event.urlAfterRedirects), // Using urlAfterRedirects for more accurate URL
        startWith(this.router.url) // Ensure initial URL is captured
      )
      .subscribe((currentUrl) => {
        this.selectedMenuButton = this.dashboardMenuButtons.find((button) =>
          currentUrl.includes(button.route)
        );
      });
  }

  dashboardMenuButtons: DashboardMenuButton[] = [
    {
      titleKey: `${TRANSLATE_KEY_PREFIX}.generalOverview`,
      icon: 'space_dashboard',
      route: 'general-overview',
    },
    {
      titleKey: `${TRANSLATE_KEY_PREFIX}.categoriesOverview`,
      icon: 'apps',
      route: 'categories-overview',
    },
    {
      titleKey: `${TRANSLATE_KEY_PREFIX}.itemsOverview`,
      icon: 'fastfood',
      route: 'items-overview',
    },
    {
      titleKey: `${TRANSLATE_KEY_PREFIX}.addOnsOverview`,
      icon: 'extension',
      route: 'add-ons-overview',
    },
    {
      titleKey: `${TRANSLATE_KEY_PREFIX}.ordersOverview`,
      icon: 'list_alt',
      route: 'orders-overview',
    },
    {
      titleKey: `${TRANSLATE_KEY_PREFIX}.scheduleOverview`,
      icon: 'calendar_month',
      route: 'schedule-overview',
    },
  ];

  isSidenavExpanded = false;

  toggleSidenav(_: boolean): void {
    this.isSidenavExpanded = !this.isSidenavExpanded;
  }

  menuButtonClicked(menuButton: DashboardMenuButton): void {
    if (menuButton.route !== this.selectedMenuButton?.route) {
      this.selectedMenuButton = menuButton;
      this.router.navigate([`/admin/${menuButton.route}`]);
    }
  }

  languageChanged(languageDirection: LanguageDirection): void {
    this.stickyLeft = languageDirection === 'ltr';
  }
}

import { Component, EventEmitter, Input, Output } from '@angular/core';
import { CartItem, CartService } from '../../services/cart.service';
import { CommonModule } from '@angular/common';
import { MaterialModule } from '../../material.module';
import { LanguageDirectionDirective } from '../../directives/language-direction.directive';
import { PropertiesTranslationPipe } from '../../pipes/properties-translation.pipe';
import { Observable } from 'rxjs';
import { LanguageType, LanguageService } from '../../services/lang.service';
import { TranslateModule } from '@ngx-translate/core';
import { AddOnUuidsToAddOnsPipe } from '../../pipes/add-on-uuids-to-add-ons.pipe';
import { PreviewItemCostPipe } from '../../pipes/preview-item-cost.pipe';

interface GroupedCartItem {
  addOns: string[];
  permutationUuids: string[];
  items: CartItemPermutation[];
  count: number;
}

interface CartItemPermutation {
  id: string;
  addOns: string[];
}

@Component({
  selector: 'app-item-preview',
  imports: [
    CommonModule,
    MaterialModule,
    LanguageDirectionDirective,
    PropertiesTranslationPipe,
    AddOnUuidsToAddOnsPipe,
    PreviewItemCostPipe,
    TranslateModule,
  ],
  templateUrl: './item-preview.component.html',
  styleUrl: './item-preview.component.scss',
})
export class ItemPreviewComponent {
  // TODO: change to group by add-ons uuids
  private _cartItem: CartItem | undefined;
  @Input() set cartItem(value: CartItem) {
    this._cartItem = value;
    this.groupedCartItem = this.groupSimilarItems();
  }
  get cartItem(): CartItem | undefined {
    return this._cartItem;
  }
  @Input() expanded = false;

  @Output() opened = new EventEmitter<number>();

  public lang$: Observable<LanguageType> = new Observable<LanguageType>();
  public groupedCartItem: GroupedCartItem[] = [];

  constructor(
    private languageService: LanguageService,
    private cartService: CartService
  ) {}

  ngOnInit(): void {
    this.lang$ = this.languageService.currentLanguage$;
  }

  groupSimilarItems(): GroupedCartItem[] {
    if (!this.cartItem?.items) return [];

    const groupMap = new Map<string, GroupedCartItem>();

    this.cartItem.items.forEach((addOns: string[], id: string) => {
      console.log(id, addOns);

      const sortedKey = [...addOns].sort().join(',');
      console.log(sortedKey);

      if (!groupMap.has(sortedKey)) {
        groupMap.set(sortedKey, {
          addOns: [...addOns].sort(),
          permutationUuids: [id],
          items: [],
          count: 0,
        });
      }

      const group = groupMap.get(sortedKey)!;
      group.items.push({ id, addOns });
      group.count++;
      group.permutationUuids.push(id);
    });

    return Array.from(groupMap.values());
  }

  removeItemGroup(group: GroupedCartItem): void {
    if (!this.cartItem?.itemUuid) return;

    group.items.forEach((item) => {
      this.cartService.removeVariant(this.cartItem!.itemUuid, item.id);
    });
    this.groupedCartItem = this.groupSimilarItems();
  }
}

import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { BehaviorSubject, Observable, catchError, map, of, tap } from 'rxjs';
