import { Injectable } from '@angular/core';
import { BehaviorSubject, Observable } from 'rxjs';

export type NotificationType = 'INFO' | 'SUCCESS' | 'WARNING' | 'ERROR';

export interface Notification {
    type: NotificationType;
    message: string;
}

@Injectable({
  providedIn: 'root',
})
export class NotificationsService {

  private _notification$ = new BehaviorSubject<Notification | null>(null);

  get notification$(): Observable<Notification | null> {
    return this._notification$.asObservable();
  }

  setNotification(notification: Notification | null): void {
    this._notification$.next(notification);
  }
}

import {
  trigger,
  state,
  style,
  transition,
  animate,
} from '@angular/animations';
import { Component, OnDestroy } from '@angular/core';
import { Subject, takeUntil } from 'rxjs';
import { LanguageService } from '../../services/lang.service';
import {
  NotificationsService,
  NotificationType,
  Notification,
} from '../../services/notifications.service';
import { TranslateModule, TranslateService } from '@ngx-translate/core';
import { CommonModule } from '@angular/common';
import { MaterialModule } from '../../material.module';

type NotificationTitle = 'info' | 'success' | 'warning' | 'error';
type NotificationIcon =
  | 'info_outline'
  | 'done'
  | 'warning_amber_outline'
  | 'close_outline';
type NotificationColor = '#4070f4' | '#12c99b' | '#f2a600' | '#e41749';

interface NotificationConfigurations {
  title: NotificationTitle;
  icon: NotificationIcon;
  color: NotificationColor;
}

@Component({
  selector: 'app-notification',
  imports: [CommonModule, MaterialModule, TranslateModule],
  templateUrl: './notification.component.html',
  styleUrls: ['./notification.component.scss'],
  animations: [
    trigger('showHideNotification', [
      state(
        'hide',
        style({
          transform: 'translateX(calc(100vw + 30px))',
        })
      ),
      state(
        'show',
        style({
          transform: 'translateX(0)',
        })
      ),
      transition('show => hide', [animate('0.5s')]),
      transition('hide => show', [animate('0.2s')]),
    ]),
  ],
})
// export class NotificationComponent {
export class NotificationComponent implements OnDestroy {
  private readonly NOTIFICATIONS = new Map<
    NotificationType,
    NotificationConfigurations
  >([
    [
      'INFO',
      {
        title: 'info',
        color: '#4070f4',
        icon: 'info_outline',
      },
    ],
    [
      'ERROR',
      {
        title: 'error',
        color: '#e41749',
        icon: 'close_outline',
      },
    ],
    [
      'SUCCESS',
      {
        title: 'success',
        color: '#12c99b',
        icon: 'done',
      },
    ],
    [
      'WARNING',
      {
        title: 'warning',
        color: '#f2a600',
        icon: 'warning_amber_outline',
      },
    ],
  ]);
  show = false;
  showNotification: 'hide' | 'show' = 'hide';
  isRtl = false;
  title: NotificationTitle | undefined;
  icon: NotificationIcon | undefined;
  color: NotificationColor | undefined;
  message: string | undefined;
  private notificationShowId: NodeJS.Timeout | undefined;
  private destroy$: Subject<void> = new Subject();
  constructor(
    private languageService: LanguageService,
    private notificationsService: NotificationsService,
    private translate: TranslateService
  ) {
    this.languageService.rtl$
      .pipe(takeUntil(this.destroy$))
      .subscribe((rtl) => (this.isRtl = rtl));
    this.notificationsService.notification$
      .pipe(takeUntil(this.destroy$))
      .subscribe((notification: Notification | null) => {
        if (!!notification) {
          const currentNotification = this.NOTIFICATIONS.get(notification.type);
          this.message = notification.message;
          this.title = this.translate.instant(
            `notifications.title.${currentNotification?.title}`
          );
          this.icon = currentNotification?.icon;
          this.color = currentNotification?.color;
          this.show = true;
          setTimeout(() => (this.showNotification = 'show'), 100);
          this.notificationShowId = setTimeout(() => {
            this.showNotification = 'hide';
            this.notificationsService.setNotification(null);
            setTimeout(() => (this.show = false), 1000);
          }, 500000);
        }
      });
  }

  ngOnDestroy(): void {
    this.destroy$.next();
    this.destroy$.complete();
  }

  close(): void {
    this.showNotification = 'hide';
    setTimeout(() => (this.show = false), 1000);
    clearTimeout(this.notificationShowId);
    this.notificationsService.setNotification(null);
  }
}

import { Injectable } from '@angular/core';
import { Observable, from } from 'rxjs';

export interface ReceiptData {
  headerInfo?: {
    storeName?: string;
    storeAddress?: string;
    storeCity?: string;
    logo?: string;
  };
  date?: string;
  referenceNumber?: string;
  transactionType?: string;
  items?: Array<{
    sku?: string;
    description?: string;
    quantity?: number;
    unitPrice?: number;
    price?: number;
  }>;
  subtotal?: number;
  tax?: number;
  total?: number;
  paymentInfo?: {
    method?: string;
    amount?: string;
    cardNumber?: string;
    authCode?: string;
  };
  footerInfo?: {
    refundPolicy?: string;
    returnPolicy?: string;
    additionalInfo?: string;
    thankYouMessage?: string;
  };
}

export interface PrintResult {
  success: boolean;
  timestamp: number;
}

@Injectable({
  providedIn: 'root',
})
export class ReceiptPrinterService {
  private readonly androidPrinter: any;

  constructor() {
    // Check if the Android interface is available
    this.androidPrinter = (window as any).AndroidPrinter;

    // Setup callback function for print results
    if (!('angularPrintCallback' in window)) {
      (window as any).angularPrintCallback = (result: PrintResult) => {
        this.handlePrintResult(result);
      };
    }
  }

  /**
   * Check if printer is available
   */
  isPrinterAvailable(): Observable<boolean> {
    return new Observable((observer) => {
      if (!this.androidPrinter) {
        observer.next(false);
        observer.complete();
        return;
      }

      try {
        const result = JSON.parse(this.androidPrinter.isPrinterAvailable());
        observer.next(result.available);
        observer.complete();
      } catch (error) {
        console.error('Error checking printer availability:', error);
        observer.next(false);
        observer.complete();
      }
    });
  }

  /**
   * Print a receipt using the connected printer
   * @param receiptData The data for the receipt
   */
  printReceipt(receiptData: ReceiptData): Observable<boolean> {
    return new Observable((observer) => {
      if (!this.androidPrinter) {
        console.error('Android printer interface not available');
        observer.next(false);
        observer.complete();
        return;
      }

      try {
        // Store the observer for callback response
        this.currentPrintObserver = observer;

        // Send print request to Android
        const response = JSON.parse(
          this.androidPrinter.printReceipt(JSON.stringify(receiptData))
        );

        console.log('Print job submitted:', response);

        // We don't complete the observer here, it will be completed when
        // the callback is received from Android
      } catch (error) {
        console.error('Error sending print request:', error);
        observer.next(false);
        observer.complete();
      }
    });
  }

  // Keep track of current print job observer
  private currentPrintObserver: any = null;

  /**
   * Handle the print result callback from Android
   */
  private handlePrintResult(result: PrintResult): void {
    console.log('Print result received:', result);

    if (this.currentPrintObserver) {
      this.currentPrintObserver.next(result.success);
      this.currentPrintObserver.complete();
      this.currentPrintObserver = null;
    }
  }
}

