import {
  ChangeDetectionStrategy,
  Component,
  inject,
  Input,
  OnInit,
} from '@angular/core';
import { Item } from '../travler.models';
import { Observable } from 'rxjs';
import { LanguageService, LanguageType } from '../../services/lang.service';
import { CommonModule } from '@angular/common';
import { PropertiesTranslationPipe } from '../../pipes/properties-translation.pipe';
import { MaterialModule } from '../../material.module';
import { LanguageDirectionDirective } from '../../directives/language-direction.directive';
import { CartService } from '../../services/cart.service';
import { MatDialog } from '@angular/material/dialog';
import { ItemsSummaryModalComponent } from '../items-summary-modal/items-summary-modal.component';
import { ItemAddOnsSelectionsComponent } from '../item-add-ons-selections/item-add-ons-selections.component';

@Component({
  selector: 'app-item',
  imports: [
    CommonModule,
    MaterialModule,
    LanguageDirectionDirective,
    PropertiesTranslationPipe,
  ],
  templateUrl: './item.component.html',
  styleUrl: './item.component.scss',
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class ItemComponent implements OnInit {
  @Input() item: Item | undefined;

  readonly dialog = inject(MatDialog);

  public lang$: Observable<LanguageType> | undefined;
  public itemCounter$: Observable<number> | undefined;

  constructor(
    private languageService: LanguageService,
    private cartService: CartService
  ) {}

  ngOnInit(): void {
    this.lang$ = this.languageService.currentLanguage$;
    this.itemCounter$ = this.cartService.getItemCount(this.item?.uuid || '');
  }

  addItemToCart(item?: Item): void {
    if (item?.availableAddOnUuids && item.availableAddOnUuids.length) {
      const dialogRef = this.dialog.open(ItemAddOnsSelectionsComponent, {
        // width: '80%',
        data: {
          availableAddOnUuids: item?.availableAddOnUuids,
          addOnPrice: item?.addOnPrice,
          freeAvailableAddOns: item?.freeAvailableAddOns,
        },
      });

      dialogRef.afterClosed().subscribe((selectedAddOnUuids?: string[]) => {
        if (selectedAddOnUuids) {
          // Handle the selected add-ons UUIDs
          console.log('Selected Add-Ons:', selectedAddOnUuids);
          this.cartService.addItem(item, selectedAddOnUuids);
        }
      });
    } else if (item) {
      this.cartService.addItem(item);
    }
  }

  showSelectedItems() {
    if (!this.item) {
      return;
    }
    const itemUuid = this.item.uuid;

    const cartItem = this.cartService.getCartItem(itemUuid);
    if (cartItem) {
      const dialogRef = this.dialog.open(ItemsSummaryModalComponent, {
        width: '80%',
        data: cartItem,
      });

      dialogRef.afterClosed().subscribe((result) => {
        console.log(`Dialog result: ${result}`);
      });
    }
  }
}

import { CommonModule } from '@angular/common';
import { Component, Inject, OnInit } from '@angular/core';
import {
  FormBuilder,
  FormControl,
  FormGroup,
  FormsModule,
  ReactiveFormsModule,
  Validators,
} from '@angular/forms';
import { MAT_DIALOG_DATA, MatDialogRef } from '@angular/material/dialog';
import { combineLatest, map, Observable, of, startWith, take, tap } from 'rxjs';
import { MaterialModule } from '../../material.module';
import {
  LanguageDirection,
  LanguageService,
  LanguageType,
} from '../../services/lang.service';
import { AddOn, Category, Item } from '../../travler/travler.models';
import { LoadFileComponent } from '../../components/load-file/load-file.component';
import { LanguageDirectionDirective } from '../../directives/language-direction.directive';
import { TranslateModule } from '@ngx-translate/core';
import { ItemsService } from '../../services/items.service';
import { PropertiesTranslationPipe } from '../../pipes/properties-translation.pipe';
import { MatChipInputEvent } from '@angular/material/chips';
import { MatAutocompleteSelectedEvent } from '@angular/material/autocomplete';

@Component({
  selector: 'app-item-form',
  imports: [
    CommonModule,
    MaterialModule,
    FormsModule,
    ReactiveFormsModule,
    LoadFileComponent,
    LanguageDirectionDirective,
    TranslateModule,
    PropertiesTranslationPipe,
  ],
  templateUrl: './item-form.component.html',
  styleUrl: './item-form.component.scss',
})
export class ItemFormComponent implements OnInit {
  public lang$: Observable<LanguageType> = new Observable<LanguageType>();
  public itemForm!: FormGroup;
  public itemImage: File | undefined;
  public dir: LanguageDirection = 'ltr';
  public availableAddOns: AddOn[] = [];
  public categories: Category[] = [];

  public addOnCtrl = new FormControl();
  public filteredAddOns: Observable<AddOn[]> | undefined;
  cbPennding = false;

  constructor(
    @Inject(MAT_DIALOG_DATA)
    public data: {
      item?: Item;
      categoryUuid?: string;
      cb: (data: { item: Item; image?: File }) => Observable<any>;
    },
    private dialogRef: MatDialogRef<ItemFormComponent>,
    private languageService: LanguageService,
    private itemsService: ItemsService,
    private fb: FormBuilder
  ) {
    this.lang$ = this.languageService.currentLanguage$;

    this.filteredAddOns = this.addOnCtrl.valueChanges.pipe(
      startWith(null),
      map((addOn: string | null) =>
        addOn
          ? this._filterAddOns(addOn)
          : this.availableAddOns
              .slice()
              .filter(
                (addOn) =>
                  !this.itemForm
                    .get('availableAddOnUuids')
                    ?.value?.includes(addOn.uuid)
              )
      )
    );
  }

  ngOnInit(): void {
    this.loadAddOns();
    this.initForm();
  }

  private loadAddOns(): void {
    combineLatest([
      this.itemsService.addOns$,
      this.itemsService.allItems$,
    ]).subscribe(([addOns, categories]) => {
      this.categories = categories;

      this.availableAddOns = Array.from(addOns.values());
    });
  }

  add(event: MatChipInputEvent): void {
    const value = (event.value || '').trim();

    if (value) {
      const addOn = this.availableAddOns.find((addOn) => addOn.uuid === value);
      if (addOn) {
        this.itemForm.get('availableAddOnUuids')?.value?.push(addOn.uuid);
        this.addOnCtrl.setValue(null);
      }
    }
  }

  remove(addOnUuid: string): void {
    const selectedAddOns = this.itemForm.get('availableAddOnUuids')?.value;
    const index = selectedAddOns.indexOf(addOnUuid);

    if (index >= 0) {
      selectedAddOns.splice(index, 1);
    }
  }

  selected(event: MatAutocompleteSelectedEvent): void {
    const addOnUuid = event.option.value;
    if (!this.itemForm.get('availableAddOnUuids')?.value?.includes(addOnUuid)) {
      this.itemForm.get('availableAddOnUuids')?.value.push(addOnUuid);
    }
    this.addOnCtrl.setValue(null);
  }

  onSubmit(): void {
    if (
      this.itemForm.valid &&
      (this.itemForm.controls['imageUrl'].value || this.itemImage)
    ) {
      const formData = {
        item: this.itemForm.value,
        image: this.itemImage,
      };

      this.cbPennding = true;
      this.dialogRef.disableClose = true;

      this.data
        .cb(formData)
        .pipe(take(1))
        .subscribe(() => {
          this.dialogRef.close(true);
        });
    }
  }

  imageChanged(file: File): void {
    const formData = new FormData();
    formData.set('file', file, file.name);
    this.itemImage = file;
  }

  imageRemoved(): void {
    this.itemImage = undefined;
  }

  languageChanged(languageDirection: LanguageDirection): void {
    this.dir = languageDirection;
  }

  getAddOnName(uuid: string): AddOn | undefined {
    const addOn = this.availableAddOns.find((addOn) => addOn.uuid === uuid);
    return addOn;
  }

  private initForm(): void {
    this.itemForm = this.fb.group({
      uuid: [this.data.item?.uuid || ''],
      categoryId: [this.data.categoryUuid || '', Validators.required],
      enName: [this.data.item?.enName || '', Validators.required],
      heName: [this.data.item?.heName || '', Validators.required],
      esName: [this.data.item?.esName || '', Validators.required],
      enDetails: [this.data.item?.enDetails || '', Validators.required],
      heDetails: [this.data.item?.heDetails || '', Validators.required],
      esDetails: [this.data.item?.esDetails || '', Validators.required],
      imageUrl: [this.data.item?.imageUrl || ''],
      price: [
        this.data.item?.price || 0,
        [Validators.required, Validators.min(0)],
      ],
      availableAddOnUuids: [this.data.item?.availableAddOnUuids || []],
      addOnPrice: [this.data.item?.addOnPrice || 0],
      freeAvailableAddOns: [this.data.item?.freeAvailableAddOns || 0],
    });
  }

  private _filterAddOns(value: string): AddOn[] {
    const filterValue = value.toLowerCase();
    return this.availableAddOns.filter(
      (addOn) =>
        addOn.enName.toLowerCase().includes(filterValue) ||
        addOn.heName.toLowerCase().includes(filterValue) ||
        addOn.esName.toLowerCase().includes(filterValue)
    );
  }
}

import { Component, EventEmitter, Input, Output } from '@angular/core';
import { CartItem, CartService } from '../../services/cart.service';
import { CommonModule } from '@angular/common';
import { MaterialModule } from '../../material.module';
import { LanguageDirectionDirective } from '../../directives/language-direction.directive';
import { PropertiesTranslationPipe } from '../../pipes/properties-translation.pipe';
import { Observable } from 'rxjs';
import { LanguageType, LanguageService } from '../../services/lang.service';
import { TranslateModule } from '@ngx-translate/core';
import { AddOnUuidsToAddOnsPipe } from '../../pipes/add-on-uuids-to-add-ons.pipe';
import { PreviewItemCostPipe } from '../../pipes/preview-item-cost.pipe';

interface GroupedCartItem {
  addOns: string[];
  permutationUuids: string[];
  items: CartItemPermutation[];
  count: number;
}

interface CartItemPermutation {
  id: string;
  addOns: string[];
}

@Component({
  selector: 'app-item-preview',
  imports: [
    CommonModule,
    MaterialModule,
    LanguageDirectionDirective,
    PropertiesTranslationPipe,
    AddOnUuidsToAddOnsPipe,
    PreviewItemCostPipe,
    TranslateModule,
  ],
  templateUrl: './item-preview.component.html',
  styleUrl: './item-preview.component.scss',
})
export class ItemPreviewComponent {
  // TODO: change to group by add-ons uuids
  private _cartItem: CartItem | undefined;
  @Input() set cartItem(value: CartItem) {
    this._cartItem = value;
    this.groupedCartItem = this.groupSimilarItems();
  }
  get cartItem(): CartItem | undefined {
    return this._cartItem;
  }
  @Input() expanded = false;

  @Output() opened = new EventEmitter<number>();

  public lang$: Observable<LanguageType> = new Observable<LanguageType>();
  public groupedCartItem: GroupedCartItem[] = [];

  constructor(
    private languageService: LanguageService,
    private cartService: CartService
  ) {}

  ngOnInit(): void {
    this.lang$ = this.languageService.currentLanguage$;
  }

  groupSimilarItems(): GroupedCartItem[] {
    if (!this.cartItem?.items) return [];

    const groupMap = new Map<string, GroupedCartItem>();

    this.cartItem.items.forEach((addOns: string[], id: string) => {
      console.log(id, addOns);

      const sortedKey = [...addOns].sort().join(',');
      console.log(sortedKey);

      if (!groupMap.has(sortedKey)) {
        groupMap.set(sortedKey, {
          addOns: [...addOns].sort(),
          permutationUuids: [id],
          items: [],
          count: 0,
        });
      }

      const group = groupMap.get(sortedKey)!;
      group.items.push({ id, addOns });
      group.count++;
      group.permutationUuids.push(id);
    });

    return Array.from(groupMap.values());
  }

  removeItemGroup(group: GroupedCartItem): void {
    if (!this.cartItem?.itemUuid) return;

    group.items.forEach((item) => {
      this.cartService.removeVariant(this.cartItem!.itemUuid, item.id);
    });
    this.groupedCartItem = this.groupSimilarItems();
  }
}

import { Component, inject } from '@angular/core';
import { combineLatest, delay, Observable, startWith, take } from 'rxjs';
import { ItemsService } from '../../services/items.service';
import { Category, Item } from '../../travler/travler.models';
import { MaterialModule } from '../../material.module';
import { CommonModule } from '@angular/common';
import { FormControl, FormsModule, ReactiveFormsModule } from '@angular/forms';
import { TranslateModule } from '@ngx-translate/core';
import { MatDialog } from '@angular/material/dialog';
import { ItemFormComponent } from '../item-form/item-form.component';
import { DeleteConfirmationModalComponent } from '../delete-confirmation-modal/delete-confirmation-modal.component';
import { LanguageDirectionDirective } from '../../directives/language-direction.directive';
import { PropertiesTranslationPipe } from '../../pipes/properties-translation.pipe';
import {
  LanguageDirection,
  LanguageService,
  LanguageType,
} from '../../services/lang.service';

@Component({
  selector: 'app-items-overview',
  imports: [
    CommonModule,
    MaterialModule,
    FormsModule,
    ReactiveFormsModule,
    TranslateModule,
    PropertiesTranslationPipe,
    LanguageDirectionDirective,
  ],
  templateUrl: './items-overview.component.html',
  styleUrl: './items-overview.component.scss',
})
export class ItemsOverviewComponent {
  readonly dialog = inject(MatDialog);
  public lang$: Observable<LanguageType> = new Observable<LanguageType>();

  viewMode: 'grid' | 'table' = 'grid';
  searchTerm = new FormControl('');
  selectedCategory = new FormControl<Category | null>(null);

  displayedColumns: string[] = [
    'name',
    'price',
    'addOns',
    'addOnPrice',
    'freeAddOnAllowed',
    'image',
    'actions',
  ];

  items: Item[] = [];
  categories: Category[] = [];
  filteredItems: Item[] = [];
  dir: LanguageDirection = 'ltr';

  constructor(
    private itemsService: ItemsService,
    private languageService: LanguageService
  ) {
    this.lang$ = this.languageService.currentLanguage$;
    this.itemsService.allItems$.subscribe((categories) => {
      this.categories = categories;
      this.items = categories.map((category) => category.items).flat();
      this.filteredItems = categories.map((category) => category.items).flat();
    });

    combineLatest([
      this.selectedCategory.valueChanges.pipe(startWith(null)),
      this.searchTerm.valueChanges.pipe(startWith('')),
    ]).subscribe(([selectedCategory, term]) => {
      const value = term?.toLowerCase() || '';
      if (!selectedCategory) {
        this.filteredItems = this.items.filter((item) => {
          return (
            item.enName.toLowerCase().includes(value) ||
            item.esName.toLowerCase().includes(value) ||
            item.heName.toLowerCase().includes(value)
          );
        });
      } else {
        this.filteredItems = this.categories
          .filter(
            (currentCategory) => currentCategory.uuid === selectedCategory.uuid
          )
          .map((category) => category.items)
          .flat()
          .filter((item) => {
            return (
              item.enName.toLowerCase().includes(value) ||
              item.esName.toLowerCase().includes(value) ||
              item.heName.toLowerCase().includes(value)
            );
          });
      }
    });
  }

  addNewItem(): void {
    const dialogRef = this.dialog.open(ItemFormComponent, {
      width: 'auto',
      maxWidth: '90%',
      data: {
        cb: (data: { item: Item; image: File }) =>
          this.itemsService.createItem(data.item, data.image),
      },
    });

    dialogRef.afterClosed().pipe(take(1)).subscribe();
  }

  editItem(item: Item) {
    const categoryUuid = this.categories.find((cat) =>
      cat.items.some((ite) => ite.uuid === item.uuid)
    )?.uuid;
    const dialogRef = this.dialog.open(ItemFormComponent, {
      width: 'auto',
      maxWidth: '90%',
      data: {
        item,
        categoryUuid,
        cb: (data: { item: Item; image: File }) =>
          this.itemsService.editItem(data.item, data.image),
      },
    });

    dialogRef.afterClosed().pipe(take(1)).subscribe();
  }

  deleteItem(item: Item) {
    const dialogRef = this.dialog.open(DeleteConfirmationModalComponent, {
      autoFocus: false,
      data: {
        type: 'item',
        cb: (item: Item) =>
          this.itemsService.deleteItem(item).pipe(delay(2000)),
        objectToDelete: item,
      },
    });

    dialogRef.afterClosed().pipe(take(1)).subscribe();
  }

  languageChanged(languageDirection: LanguageDirection): void {
    this.dir = languageDirection;
  }
}

